import React from 'react';
import Navbar from './Navbar';
import './homepage.css';
import image from './images/bg2.png';
import Slider from './Slider';
import SliderTwo from './Slider2';
import img1 from '../assets/img1.jpg'
import img2 from '../assets/img2.jpg'
import img3 from '../assets/img3.webp'

const content = [
    {
        img: img1,
    },
    {
        img: img2,
    },
    {
        img: img3,
    }
]

function Homepage() {

    const myStyle = {
        backgroundImage: `url(${image})`, backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        textAlign: 'center',
        minHeight: '100vh',
        width: '100vw'
    };
    return (
        <div class="homepage_main min-h-screen" style={myStyle}>
            <Navbar />
            {/* <div className='w-full h-full mt-24 relative'>
                <Slider />
            </div>
            <div className='w-full h-full mt-10 relative'>
                <SliderTwo />
            </div> */}

            <div class="slider">
                <span id="guidesinfo"></span>
                <span id="slide-2"></span>
                <span id="slide-3"></span>
                <div class="image-container">
                    <div class = "image1">
                        <img src={img1} class="slide" width="500"/>
                        <div class = "txt1">Build Your Own Custom PC</div>
                        <button class="btn first"><span>Let's Build</span></button>
                    </div>
                    <div class = "image2">
                        <img src={img2} class="slide" width="500"/>
                        <div class = "txt2">PRE Build PC's</div>
                        <button class="btn second"><span>Perfect</span></button>
                    </div>
                    <div class = "image3">
                        <img src={img3} class="slide" width="500"/>
                        <div class = "txt3">BUILD GUIDES</div>
                        <button class="btn third"><span>Need Help</span></button>
                    </div>
                </div>
                <div class="buttons">
                    <a href="#slide-1"></a>
                    <a href="#slide-2"></a>
                    <a href="#slide-3"></a>
                </div>
            </div>

            {/* <div class = "mainInfo">
                <div class = "userGuide">
                    <div class = "guideHeading">
                        <h1>User Guide</h1>
                    </div>
                    <div class = "guideContent">
                        <p>If you're interested in building your own custom PC, here are the essential components you'll need: <span class = "Items">CPU, Motherboard, Graphics Card, RAM, Storage, Power Supply Unit, Cooling</span>. When choosing parts, make sure they're compatible with each other, and that they suit your budget and performance requirements. Some common tools you'll need for assembly include screwdrivers, thermal paste, and cable ties. When building, take your time and follow instructions carefully to avoid damaging any components. You can find helpful tutorials here to help you through the process. <span class = "Items">GOOD LUCK!</span>
                        </p>
                    </div>
                </div>
            </div> */}
        </div>
    );
}

export default Homepage;
